import React, { useState } from "react";
import "./styleComponents.css";
import Key from "./Key";
import axios from "axios";

const NumPad = () => {
  const keys1 = ["7", "8", "9"];
  const keys2 = ["4", "5", "6"];
  const keys3 = ["1", "2", "3"];
  const keys4 = ["10", "ENTER"];
  const [erroruser, setError] = useState("");
  const [showPopup, setShowPopup] = useState(false);
  const [finishLink, setFinishLink] = useState("");

  const callPopup = (mes, finish) => {
    setShowPopup(true);
    setError(mes);
    if (finish !== "") {
      setFinishLink(finish);
    }
  };

  const handleClosePopup = () => {
    // Set showPopup to false when the popup is closed
    setShowPopup(false);
  };

  const handleCopyToClipboard = () => {
    navigator.clipboard
      .writeText(finishLink)
      .then(() => {
        const alertStyle = {
          background: "#ffd699",
          color: "#4c280f",
          border: "2px solid #4c280f",
          padding: "10px",
          borderRadius: "5px",
          textAlign: "center",
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          zIndex: "9999",
          boxShadow: "0 0 10px rgba(0, 0, 0, 0.2)",
        };

        // Create and style the alert element
        const alertElement = document.createElement("div");
        alertElement.textContent = "Link copied to clipboard!";
        Object.assign(alertElement.style, alertStyle);

        // Append the alert to the body
        document.body.appendChild(alertElement);

        // Remove the alert after 2 seconds
        setTimeout(() => {
          alertElement.remove();
        }, 2000);
      })
      .catch((error) => {
        console.error("Failed to copy: ", error);
      });
  };

  return (
    <div className="keyboard">
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <span
              className="close"
              onClick={handleClosePopup}
              errorFunction={callPopup}
            >
              &times;
            </span>
            <div className="instructions">{erroruser}</div>
            {finishLink !== "" && (
              <button onClick={handleCopyToClipboard}>Share</button>
            )}
          </div>
        </div>
      )}
      <div className="line">
        {keys1.map((key) => (
          <Key key={key} keyVal={key} errorFunction={callPopup} />
        ))}
      </div>
      <div className="line">
        {keys2.map((key) => (
          <Key key={key} keyVal={key} errorFunction={callPopup} />
        ))}
      </div>
      <div className="line">
        {keys3.map((key) => (
          <Key key={key} keyVal={key} errorFunction={callPopup} />
        ))}
      </div>
      <div className="line">
        {keys4.map((key) =>
          key === "ENTER" ? (
            <Key key={key} keyVal={key} bigKey />
          ) : (
            <Key key={key} keyVal={key} errorFunction={callPopup} />
          )
        )}
      </div>
    </div>
  );
};

export default NumPad;
