import React, { useState } from "react";
import "./styleComponents.css";

const QuestionMarkButton = ({ onClick }) => {
  const [showPopup, setShowPopup] = useState(false);

  const callPopup = () => {
    setShowPopup(true);
  };

  const handleClosePopup = () => {
    setShowPopup(false);
  };

  return (
    <div>
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <span className="close" onClick={handleClosePopup}>
              &times;
            </span>
            <div className="instructions">
            <h2>How To Play</h2>
            <p>Guess the Wordle in 6 tries.</p>
            <p>Each guess must be a valid 5-letter word.</p>
            <p>The color of the tiles will change to show how close your guess was to the word.</p>
            <h3>Examples</h3>
            <p>W is in the word and in the correct spot.</p>
            <p>I is in the word but in the wrong spot.</p>
            <p>U is not in the word in any spot.</p>
            <h3>Green Bar Graph</h3>
            <p>Log in or create a free NYT account to link your stats.</p>
            <p>A new puzzle is released daily at midnight. If you havenâ€™t already, you can sign up for our daily reminder email.</p>
          </div>
          </div>
        </div>
      )}
      <button
        className="question-mark-button"
        onClick={() => {
          onClick();
          callPopup();
        }}
        style={{ cursor: "pointer" }}
      >
        ?
      </button>
    </div>
  );
};

export default QuestionMarkButton;
