import React, { useContext, useState } from 'react'
import { AppContext } from '../App'
import axios from 'axios'


const Key = ({keyVal , bigKey}) => {

    const {board,setBoard,currentAttempt,setCurrentAttempt} = useContext(AppContext);
    const [selction,setSelection] = useState("");
    
      async function selectLetter(e){
        e.preventDefault()
        setSelection(keyVal);
        console.log(keyVal);
          const number = String(keyVal)
          const response=[];
        try {
             response = await axios.post("http://localhost:3001/post_number", {number });
            console.log("POST request successful:", response.data);
          } catch (error) {
            console.error("Error in POST request:", error);
          }
        const arr = response.data
      //  const newArray = arr.map(value => (value === undefined ? 0 : value));
        const newBoard =[...board]
        newBoard[currentAttempt] =keyVal
        setBoard(newArray)
        setCurrentAttempt(currentAttempt+1)
    }
    //updating array and recieving from node two data at same time

  return (
    <div className='key' id={bigKey && "big"} onClick={selectLetter}>
      {keyVal}
    </div>
  )
}

export default Key
