import React, { useState } from "react";
import "./styleComponents.css";

const QuestionMarkButton = ({ onClick }) => {
  const [showPopup, setShowPopup] = useState(false);

  const callPopup = (mes) => {
    setShowPopup(true);
  };

  const handleClosePopup = () => {
    // Set showPopup to false when the popup is closed
    setShowPopup(false);
  };

  return (
    <div>
        {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <span
              className="close"
              onClick={handleClosePopup}
              errorFunction={callPopup}
            >
            </span>
          </div>
        </div>
      )}
     <button className="question-mark-button"onClick={onClick} style={{ cursor: 'pointer' }}>
      ?
    </button>
    </div>
  );
};

export default QuestionMarkButton;
