import Guess from "./components/Guess"
import NumPad from "./components/NumPad"
import Number from "./components/RandomNumber";
import React, { useState , useEffect ,createContext} from 'react';
import './App.css';
import axios from "axios"
import {boardDefault} from "../src/components/BoardDefault"
import Key from '../src/components/Key';
import Rand from '../src/components/RandomNumber';
export const AppContext = createContext();

function App() {

   // Assuming guess is a state variablE
   const [test, setTest] = useState("");
   const[rand,setRand]=useState(0);
   const[index,setIndex]=useState("");
   useEffect(() =>{
    axios.get("http://localhost:3001/post_random").then(function(response){
      setRand(response.data)
      console.log(response)
    })
   })

   const [board,setBoard] = useState(boardDefault);
  const [currentAttempt,setCurrentAttempt]= useState(0)
  return (
  <div className="App">
    <div >
      <h1> Numbering!</h1>
      <AppContext.Provider value={{board,setBoard,currentAttempt,setCurrentAttempt,rand}}>
        <div className="game">
        <Number />
        <Guess number={test} guess={"0123456789"}/>
        <NumPad />
        </div>
      </AppContext.Provider>

    </div>
  </div>
  );
}

export default App;
