import React, { useState } from "react";
import "./styleComponents.css";
import Key from "./Key";
import axios from "axios";

const NumPad = () => {
  const keys1 = ["7", "8", "9"];
  const keys2 = ["4", "5", "6"];
  const keys3 = ["1", "2", "3"];
  const keys4 = ["10", "ENTER"];
  const [erroruser, setError] = useState("");
  const [showPopup, setShowPopup] = useState(false);
  const [finishLink, setFinishLink] = useState("");

  const callPopup = (mes, finish) => {
    setShowPopup(true);
    setError(mes);
    if (finish !== "") {
      setFinishLink(finish);
    }
  };

  const handleClosePopup = () => {
    // Set showPopup to false when the popup is closed
    setShowPopup(false);
  };

  const handleCopyToClipboard = () => {
    navigator.clipboard.writeText(finishLink)
      .then(() => {
        alert("Link copied to clipboard!");
      })
      .catch((error) => {
        console.error('Failed to copy: ', error);
      });
  };


  return (
    <div className="keyboard">
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <span
              className="close"
              onClick={handleClosePopup}
              errorFunction={callPopup}
            >
              &times;
            </span>
            <div className="instructions">{erroruser}</div>
            {finishLink !== "" && <button onClick={handleCopyToClipboard}>Copy to Clipboard</button>}
          </div>
        </div>
      )}
      <div className="line">
        {keys1.map((key) => (
          <Key key={key} keyVal={key} errorFunction={callPopup} />
        ))}
      </div>
      <div className="line">
        {keys2.map((key) => (
          <Key key={key} keyVal={key} errorFunction={callPopup} />
        ))}
      </div>
      <div className="line">
        {keys3.map((key) => (
          <Key key={key} keyVal={key} errorFunction={callPopup} />
        ))}
      </div>
      <div className="line">
        {keys4.map((key) =>
          key === "ENTER" ? (
            <Key key={key} keyVal={key} bigKey />
          ) : (
            <Key key={key} keyVal={key} errorFunction={callPopup} />
          )
        )}
      </div>
    </div>
  );
};

export default NumPad;
