    const {board,setBoard,currentAttempt,setCurrentAttempt,rand} = useContext(AppContext);
    const [selction,setSelection] = useState("");
    const [prev,setPrev]=useState(false);
    
      async function selectLetter(e){
        e.preventDefault()
        setSelection(keyVal);
          const number = String(keyVal)
          const message=""
        try {
            const response = await axios.post("http://localhost:3001/post_number", {number});
            console.log("POST request successful:", response.data);
            if(response.data.inputArr != null){
                for (let i = 0; i < board.length; i++) {
                    if (response.data.inputArr[i] !== board[i]) {
                        setBoard(response.data.inputArr)
                        setCurrentAttempt(currentAttempt+1)
                        console.log("HI")
                       // setPrev(true);
                        break;
                    }
                  }
                  if(response.data.error != ""){
                  errorFunction(message);
                   console.log(response.data.error)
                  }
                
            }
            
          } catch (error) {
            console.error("Error in POST request:", error);
          }
          
        
    }
    //updating array and recieving from node two data at same time

  return (
    <div className='key' id={bigKey && "big"} onClick={selectLetter}>
      {keyVal}
    </div>
  )
}

export default Key
