import React from "react";
import "./styleComponents.css";

const QuestionMarkButton = ({ onClick }) => {
  const [showPopup, setShowPopup] = useState(false);

  const callPopup = (mes) => {
    setShowPopup(true);
    setError(mes);
  };

  const handleClosePopup = () => {
    // Set showPopup to false when the popup is closed
    setShowPopup(false);
  };



  return (
    <button
      className="question-mark-button"
      onClick={onClick}
      style={{ cursor: "pointer" }}
    >
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <span
              className="close"
              onClick={handleClosePopup}
              errorFunction={callPopup}
            >
              &times;
            </span>
            {"erroruser"}
          </div>
        </div>
      )}
    </button>
  );
};

export default QuestionMarkButton;
